package symboltable;import syntaxtree.Type;public class RamVariable {    String id;    Type type;    private static int offset_counter = 0;    private final int offset;    public RamVariable(String id, Type type)    {        this.id = id;        this.type = type;        offset = ++offset_counter;    }    public int getOffset() {        return offset;    }    public String getId() {        return id;    }    public Type type() {        return type;    }        public String toString()     {        if (type instanceof syntaxtree.IdentifierType)            return ((syntaxtree.IdentifierType) type).s + " " + id;        else            return type.getClass() + " " + id;    }} // Variable